package com.yourcompany.cafeteria.service; import javax.print.PrintService; import javax.print.PrintServiceLookup; import java.util.ArrayList; import java.util.List; public class PrinterService { public static List<String> listPrinters(){ List<String> out = new ArrayList<>(); for(PrintService s: PrintServiceLookup.lookupPrintServices(null,null)) out.add(s.getName()); return out; } }
